local floor = 0;
local firstTime = 0;

for i = 1, #puzzle do
    local char = puzzle:sub(i, i);

    if char == "(" then
        floor = floor + 1;
    elseif char == ")" then
        floor = floor - 1;
    end

	if floor == -1 then
		firstTime = i;
		break;
	end
end

print("We enter basement at: " .. firstTime);
